mapper.update:
update systemlog
set optime = #{optime,jdbcType=TIMESTAMP},
  ip = #{ip,jdbcType=VARCHAR},
  function = #{function,jdbcType=VARCHAR},
  params = #{params,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, optime, ip, function, params
from systemlog
where id = #{id,jdbcType=BIGINT}
